import { useState, useEffect } from "react";
import tutoriais from "../Services/dadosTutoriais";
import "../Styles/Pages/filmes.css";
import Sidebar from "../Components/Sidebar";

const Tutoriais = () => {
  const [busca, setBusca] = useState("");
  const [filtroTutoriais, setFiltro] = useState([]);

  useEffect(() => {
    setFiltro(
      tutoriais.filter((tutorial) => {
        return tutorial.nome.includes(busca);
      })
    );
  }, [busca]);

  return (
    <>
      <Sidebar />
      <div className="div-busca">
        <input
          className="busca"
          type="text"
          placeholder="Pesquisar pelo nome"
          onChange={(e) => {
            setBusca(e.target.value);
          }}
        />
      </div>

      <div id="container">
        {filtroTutoriais.map((tutorial) => {
          return (
            <div className="card">
              <h3>{tutorial.nome}</h3>
              <p>GÃªnero: {tutorial.genero}</p>
              <img src={tutorial.link} alt="link" />
          <p>Link: {tutorial.link}</p>
            </div>
          );
        })}
      </div>
    </>
  );
};
export default Tutoriais;




















