import { useState, useEffect } from "react";
import depoimentos from "../Services/dadosDepoimentos";
import "../Styles/Pages/filmes.css";
import Sidebar from "../Components/Sidebar";
import { Link } from "react-router-dom";

const Depoimentos = () => {
  const [busca, setBusca] = useState("");
  const [filtroDepoimentos, setFiltro] = useState([]);

  useEffect(() => {
    setFiltro(
      depoimentos.filter((depoimento) => {
        return depoimento.nome.includes(busca);
      })
    );
  }, [busca]);

  return (
    <>
      <Sidebar />
      <div className="div-busca">
        <input
          className="busca"
          type="text"
          placeholder="Pesquisar aqui"
          onChange={(e) => {
            setBusca(e.target.value);
          }}
        />
      </div>

      <div id="container">
        {filtroDepoimentos.map((depoimento) => {
          return (
            <div className="card">
              <h2>{depoimento.nome}</h2>
              <h3>{depoimento.descricao}</h3>
              <p><strong><i>{depoimento.mensagem}</i></strong></p>
              <Link to="/facebook" target="_blank">{depoimento.facebook}</Link>
              <p><Link to={{pathname:"https://www.instagram.com/"}} target="_blank">{depoimento.instagram}</Link></p>
              <img src={depoimento.foto} alt="foto" />
            </div>
          );
        })}
      </div>
    </>
  );
};
export default Depoimentos;



























